import React, { Component } from 'react'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import IconButton from '@material-ui/core/IconButton';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import DeleteIcon from '@material-ui/icons/Delete';
import axios from 'axios'
import './utils/todo.css'

const PAGINATION_URL = 'http://127.0.0.1:8001/api/posts/';


export default class ListPost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: [{
                "id": 2,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 3,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 4,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 5,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 6,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 7,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 8,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 9,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 10,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            }
            ],
            links: '',
            // links: {
            //     "next": "http://127.0.0.1:8001/api/posts/?page=2",
            //     "previous": null
            // },


        }
        // this.handleDelete = this.handleDelete.bind(this)
        // this.handleUpdate = this.handleUpdate.bind(this)

    }

    componentDidMount() {
        this.loadPosts(PAGINATION_URL)
    }

    loadPosts = async (url) => {
        await axios.get(url)
            .then((res) => {
                console.log(res.data);
                const posts = res.data.results
                this.setState({
                    posts,
                    links: res.data.links,
                })

            })
    }
    handleDelete = (id) => {
        // e.preventDefault()


        console.log(id);
        let DELETE_URL = PAGINATION_URL + id;
        console.log(DELETE_URL)




        // if (!window.confirm('Realmente deseja excluir o operador: ' + this.props.operator.nome)) {
        //     return 0;
        // } else {
            axios.delete(DELETE_URL)
                .then(res => {
                    alert("Postagem deletada com sucesso !");
                    window.scrollTo(0, 0);
                    // window.location.href = "/" + this.props.farm_id + "/operator";
                });
        // }
    }

    handleUpdate = (post) => {
        console.log(post)

    }

    prevPage = () => {
        let toPage = this.state.links.previous
        console.log(this.state.links.previous)
        this.loadPosts(toPage);
    }

    nextPage = () => {

        let toPage = this.state.links.next
        console.log(this.state.links.next)

        this.loadPosts(toPage);
    }

    render() {
        const { links } = this.state
        const { posts } = this.state

        return (
            <div>
                <h1 align="center">Lista de postagens</h1>
                <a href="http://localhost:3002/">cadastrar nova postagem</a><br /><br />

                <div className="pagination">
                    <a href="#">&laquo;</a>


                    <button disabled={links.previous === null} onClick={this.prevPage}>Anterior</button>
                    <button disabled={links.next === null} onClick={this.nextPage}>Próximo</button>
                    {/*                         
                    <a href="#" onClick={this.prevPage}>Anterior</a>
                    <a href="#" onClick={this.nextPage}>Próximo</a> */}


                    <a href="#">&raquo;</a>
                </div>

                <Table className="" aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell>Título</TableCell>
                            <TableCell>Autor</TableCell>
                            <TableCell>Mensagem</TableCell>
                            <TableCell>Data</TableCell>
                            <TableCell>Ações</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {posts.map((post) => {
                            return <>
                                <TableRow key={post.id}>

                                    <TableCell>{post.title}</TableCell>
                                    <TableCell>{post.author}</TableCell>
                                    <TableCell>{post.body}</TableCell>
                                    <TableCell>{post.publish}</TableCell>
                                    <TableCell>
                                        <IconButton aria-label="delete" onClick={() => this.handleDelete(post.id)}>
                                            <DeleteIcon />
                                        </IconButton>
                                        <IconButton aria-label="update" onClick={() => this.handleUpdate(post)}>
                                            <CloudUploadIcon />
                                        </IconButton>
                                    </TableCell>
                                </TableRow>
                            </>
                        })}

                    </TableBody>
                </Table>
            </div>
        )
    }
}
