import React, { Component } from 'react'
import axios from 'axios';


const POST_URL = 'http://127.0.0.1:8000/api/posts/';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      author: '',
      body: '',
      publish: '',
      error:'',
      success:'',

    }
    this.handleSave = this.handleSave.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }
  handleSave() {
    console.log(POST_URL);
    axios.post(POST_URL)
    .then((res) => {
        if(res.status === 200 ){
          console.log('foi');
        }
        else{
          console.log('não foi')
        }
    })
  }

  handleChange(e){
    this.setState ({
      [e.target.name]  :  e.target.value  
    })

  }

  render() {
    return (
      <div>
        <h1>Fazer um post</h1>
        <form onSubmit={this.handleSave}>
          <input type="text" name="title" onChange={this.handleChange} /><br/>
          <input type="text" name="author" onChange={this.handleChange} /><br/>
          <textarea name="body" id="" cols="30" rows="10" onChange={this.handleChange}  /><br/>
          <input type="date" name="publish" id="" onChange={this.handleChange}/><br/>
          <button>Enviar post</button>
        </form>
      </div>
    )
  }
}
