    constructor(props) {
        super(props);
        this.state = {
            posts: '',
            count: 0,

        }
    }

    componentDidMount() {
        axios.get(PAGINATION_URL)
            .then((res) => {
                this.setState({
                    posts: res.data
                })

            })
    }

