import React, { Component } from 'react'
import axios from 'axios';
import ListPost from './ListPost'


const POST_URL = 'http://127.0.0.1:8000/api/posts/';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      author: '',
      body: '',
      publish: '',
      error:'',
      success:'',

    }
    this.handleSave = this.handleSave.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }
  handleSave(e) {
    e.preventDefault();

    console.log(POST_URL);
    axios.post(POST_URL)
    .then((res) => {
      console.log(res.status);
        if(res.status === 200 ){
          console.log('foi');
          this.setState({success:true})
        }
        else{
          console.log('não foi')
          this.setState({error:true})
        }
    })
  }

  handleChange(e){
    this.setState ({
      [e.target.name]  :  e.target.value  
    })

  }

  render() {
    let error 
    let success
    if(this.state.error === true){
      error = <div>'Deu ruim amigo' </div>
    }
    if(this.state.success === true){
      success = <div>'Deu sucesso amigo' </div>
    }
    return (
      <div>
        {error}
        {success}

        <h1>Fazer um post</h1>
        <form onSubmit={this.handleSave}>
          <input type="text" name="title" onChange={this.handleChange} /><br/>
          <input type="text" name="author" onChange={this.handleChange} /><br/>
          <textarea name="body" id="" cols="30" rows="10" onChange={this.handleChange}  /><br/>
          <input type="date" name="publish" id="" onChange={this.handleChange}/><br/>
          <button>Enviar post</button>
        </form>

        <ListPost/>
      </div>
    )
  }
}
