import React, { Component } from 'react'
import axios from 'axios'

const PAGINATION_URL = 'http://127.0.0.1:8000/api/posts/';

export default class ListPost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: [{
                "id": 2,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 3,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 4,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 5,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 6,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 7,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 8,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 9,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            },
            {
                "id": 10,
                "title": "T5t3eaaaa",
                "author": "Michael Connor",
                "body": "aaaaaaaaaaaa",
                "publish": "2020-02-26T19:38:01.602653Z"
            }
            ],
            count: 0,

        }
    }

    componentWillMount() {
        console.log('will');
        console.log(PAGINATION_URL);
        axios.get(PAGINATION_URL)
            .then((res) => {
                console.log(res.data);
                this.setState({
                    posts: res.data
                })

            })
    }

    render() {
        return (
            <div>
                <table>
                    <tr>
                        <th>Título</th>
                        <th>Autor</th>
                        <th>Mensagem</th>
                        <th>Data</th>
                        <th>Ações</th>
                    </tr>
                    <tr>
                        {/* MANDAR UM MAP NESSES CARAS AQUI */}
                        {this.state.posts.map(function (post) {
                            return <>
                                <tr>

                                    <td>{post.title}</td>
                                    <td>{post.author}</td>
                                    <td>{post.body}</td>
                                    <td>{post.publish}</td>
                                    <td>
                                        <button>Del</button>
                                        <button>Edit</button>
                                    </td>
                                </tr>
                            </>
                        })}

                    </tr>
                </table>
            </div>
        )
    }
}
